def flowFile = session.get()
   if(!flowFile) return
   def json = flowFile.getAttribute("table_field_policy_json");
   def inputFolder = flowFile.getAttribute("spark.input_folder")
   def entity = flowFile.getAttribute("entity")
   def source = flowFile.getAttribute("source")
   def feedts = flowFile.getAttribute("feedts")
   def folder =  new File(inputFolder + "/"+source+"/"+entity+"/"+feedts)
   // If it doesn't exist
   if( !folder.exists() ) {
   // Create all folders
   folder.mkdirs()
   }
   def jsonFile = new File(folder,entity+"_field_policy.json")
   jsonFile.write(json)
   flowFile = session.putAttribute(flowFile,"table_field_policy_json_file",jsonFile.getCanonicalPath())
   session.transfer(flowFile, REL_SUCCESS)

